<?phpnamespace App\Http\Controllers;//require_once(public_path().'/../lib/aws-sdk-php/vendor/autoload.php');require_once(public_path().'/../lib/aws-sdk-php-8/vendor/autoload.php');use Aws\Ses\SesClient;use Aws\Exception\AwsException;class AwsController {    protected static $key = "";    protected static $secret = "";    protected static $region = "";    function __construct($key,$secret,$region) {        self::$key = $key;        self::$secret = $secret;        self::$region = $region;    }    public static function sendEmail($from, $to, $subject, $body, $cc='') {        try {            $credentials = new \Aws\Credentials\Credentials(self::$key, self::$secret);            $SesClient = new SesClient([                'version' => 'latest',                'region'  => self::$region,                'credentials' => $credentials,            ]);            // Specify a configuration set. If you do not want to use a configuration            // set, comment the following variable, and the            // 'ConfigurationSetName' => $configuration_set argument below.            $configuration_set = 'ConfigSet';            $char_set = 'UTF-8';            $mail_obj = [                'Destination' => [                    'ToAddresses' => [$to],                ],                //'ReplyToAddresses' => [$from],                'Source' => $from,                'Message' => [                    'Body' => [                        'Html' => [                            'Charset' => $char_set,                            'Data' => $body,                        ]/*,                        'Text' => [                            'Charset' => $char_set,                            'Data' => $plaintext_body,                        ],*/                    ],                    'Subject' => [                        'Charset' => $char_set,                        'Data' => $subject,                    ],                ]            ];            if($cc!=''){                $mail_obj['Destination']['CcAddresses'] = [$cc];            }            $result = $SesClient->sendEmail($mail_obj);            $messageId = $result['MessageId'];            return true;        } catch (AwsException $e) {            // output error message if fails            echo $e->getMessage();            echo("The email was not sent. Error message: ".$e->getAwsErrorMessage()."\n");            echo "\n";        }        return false;    }}